mapper.select:
select order_details.id, order_details.order_id orderId,
  car_id carId, customer_id customerId,
  e.name employeeName, car_number carNumber,
  create_time createTime, update_time updateTime, o.status
from order_details
  inner join `order` o on order_details.order_id = o.id
  inner join employee e on o.employee_id = e.id



      order_details.id = #{id}



        and order_details.order_id = #{orderId}


        and customer_id = #{customerId}


        and car_id = #{carId}


        and e.name = #{employeeName}


        and o.status = #{status}





  order by ${orderBy}

-- 利润 SELECT e.date, income, expenditure, income-expenditure profit FROM base_month b INNER JOIN ( -- 收入 SELECT date,IFNULL(SUM(total_price),0) income FROM base_month LEFT JOIN `order` ON date = date_format(pay_time,'%Y-%m')
WHERE date <= #{end} AND date >= #{start}
GROUP BY date ) i ON i.date = b.date INNER JOIN ( -- 支出 SELECT date,IFNULL(SUM((repertory+IFNULL(num,0))*price),0) expenditure FROM base_month LEFT JOIN car ON date = date_format(create_time,'%Y-%m') LEFT JOIN ( SELECT car_id,SUM(car_number) num FROM order_details GROUP BY car_id ) o on o.car_id = car.id
WHERE date <= #{end} AND date >= #{start}
GROUP BY date ) e ON e.date = b.date GROUP BY e.date

mapper.update:
update order_details
set order_id = #{orderId,jdbcType=BIGINT},
  car_id = #{carId,jdbcType=BIGINT},
  car_number = #{carNumber,jdbcType=INTEGER}
where id = #{id,jdbcType=VARCHAR}
